import 'package:flutter/material.dart';

void main() {
  runApp(const ImageSliderApp());
}

class ImageSliderApp extends StatelessWidget {
  const ImageSliderApp({Key? key}) : super(key: key);

  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      debugShowCheckedModeBanner: false,
      title: 'Imagenes',
      home: const ImageSliderPage(),
    );
  }
}

class ImageSliderPage extends StatefulWidget {
  const ImageSliderPage({Key? key}) : super(key: key);

  @override
  _ImageSliderPageState createState() => _ImageSliderPageState();
}

class _ImageSliderPageState extends State<ImageSliderPage> {
  final List<String> _imageUrls = [
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
    'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRL081-lM0fZHVOVjZFRJ-nJFTXr-bvHCeIgA&s',
    'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTPuSIKTTTX1ibxQkv7YE6uEYu8tAmNk78aow&s',
  ];

  int _currentPageIndex = 0;

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text('Imagenes'),
        backgroundColor: Color.fromARGB(255, 59, 190, 223),
        foregroundColor: Colors.white,
      ),
      body: Center(
        child: Column(
          mainAxisAlignment: MainAxisAlignment.center,
          children: [
            SizedBox(
              height: 300,
              child: PageView(
                onPageChanged: (int page) {
                  setState(() {
                    _currentPageIndex = page;
                  });
                },
                children: _imageUrls.map((imageUrl) {
                  return Image.network(
                    imageUrl,
                    fit: BoxFit.cover,
                  );
                }).toList(),
              ),
            ),
            const SizedBox(height: 20.0),
            Text(
              'Imagen ${_currentPageIndex + 1} de ${_imageUrls.length}',
            
              style: const TextStyle(fontSize: 16.0),
            ),
          ],
        ),
      ),
    );
  }
}
